-- 增加身份宝 --
ALTER TABLE `sys_user`
    ADD COLUMN `mfa` VARCHAR(16) NULL DEFAULT NULL COMMENT '多因素认证（谷歌验证码）' AFTER `pwd_update_date`;

-- 修改全局参数长度 --
ALTER TABLE `sys_config`
    CHANGE COLUMN `config_value` `config_value` VARCHAR(2000) NULL DEFAULT '' COMMENT '参数键值' ;

-- 增加全局参数 --
